// Get related content based on keywords and category
export const getRelatedContent = (currentId, limit = 3) => {
  const current = searchIndex.find(item => item.id === currentId)
  if (!current) return []

  const scored = searchIndex
    .filter(item => item.id !== currentId)
    .map(item => {
      let score = 0

      // Same category = high relevance
      if (item.category === current.category) score += 10

      // Same type = some relevance
      if (item.type === current.type) score += 5

      // Keyword overlap
      const sharedKeywords = current.keywords.filter(kw =>
        item.keywords.includes(kw) ||
        item.title.toLowerCase().includes(kw.toLowerCase()) ||
        item.description.toLowerCase().includes(kw.toLowerCase())
      )
      score += sharedKeywords.length * 3

      return { ...item, score }
    })
    .filter(item => item.score > 0)
    .sort((a, b) => b.score - a.score)
    .slice(0, limit)

  return scored
}

export default searchIndex
